import {CartContext} from '../Global/CartContext'



const Cart = () => {
    const {shoppingCart, totalPrice, qty, dispatch} = useContext(CartContext);

  return(
    <div className="cart-container">
      <div className="cart-details"
       style={{marginTop: '100px' ,color: 'darkcyan'}}>
     <h2>Shopping Cart</h2>
     </div>
    </div>
  )
}
export default Cart; 



