import React, {useContext} from 'react';
import {CartContext} from '../Global/CartContext'



const Cart = () => {
    const {shoppingCart, totalPrice, qty, dispatch} = useContext(CartContext);

  return(
    <div className="cart-container">
      <div className="cart-details"
       style={{marginTop: '100px'}}>
        {shoppingCart.length > 0 ? 
        shoppingCart.map(cart => (<div className="cart" key={cart.id}>
          <span className="cart-image"><img src={cart.image} alt="not found" /></span>
          <span className="cart-product-name">{cart.name}</span>
          <span className="cart-product-price">{cart.price}</span>
          <span className="inc"><i className="fas fa-plus"></i></span>
          <span className="product-quantity">{cart.quantity}</span>
          <span className="dec"><i className="fas fa-minus"></i></span>
          <span className="product-total-price">500.00</span>
          <span className="delete-product"><i className="fas fa-trash"></i></span>
        </div>
        )) 
        : 'Sorry your cart is emty'}
     </div>
    </div>
  )
}
export default Cart;
