import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, Outlet, useParams, }
from "react-router-dom";
import "./App.css"
import Cart from './Cart'


export default function App() {
  return ( 

  <Router>
    <nav>
      <Link className="text-decoration" to="/">Home</Link>
      <Link className="text-decoration" to="product">Product</Link>
      <Link className="text-decoration" to="cart">Cart</Link>
    </nav>
    <Routes className="text-decoration">
      <Route path="/" element={<Home />} />

      <Route path="product" element={<Product />} >
      <Route path="/" element={<LaunchIndex />} />
      <Route path=":slug" element={<LaunchShoe />} />
      <Route path=":slug" element={<Cart />} />

    </Route>
      <Route path="*" element={<NotFound /> }/>
    </Routes>
  </Router>
  );
} 

function NotFound(){
  return ( 
   <div>
     <h1>Not Found!</h1>
     <p>Sorry your page is not found!</p>
   </div>
  )
}

function Home(){
  return ( 
   <div>
     <h1>Welcome to Shoe Store</h1>
     <img src="https://image.shutterstock.com/image-photo/sport-shoes-260nw-565500208.jpg"
      height="650" width="1800" alt="shoe store" />
   </div>
  )
}
function Product(){
  return (
   <div>
     <h1>Product</h1>
     <Outlet />
   </div>
  );
}

function LaunchIndex() {
  const [cart, setCart] = useState([]);

  const addToCart = (shoes) => {
       setCart([...cart, shoes]);
  };
  const goToCart = (cart) => {

      return(
        <div>
          <Cart />
        </div>

      )
    };


  return (
   <div>

          <button onClick={() => goToCart(<Link to=""></Link>)}
           className="header">Go To Cart ({cart.length}) </button>

    <ul className="productContainer">
       {Object.entries(shoes).map(([slug, { name, img, price }]) => (
         <li key={slug} className="link">
           <h3>{name}</h3>
           <Link to={`/product/${slug}`}  >
           <img src={img} alt={name} height="300" />
           <h2>{price}</h2>
           </Link>

           <button className="btn" onClick={() => addToCart(shoes)}>Add to Cart</button>
         </li>
       ))}
     </ul>
    </div>
  )
 } 


function LaunchShoe() {
  const { slug } = useParams();
  const shoe = shoes[slug];

if(!shoe) {
  return(
    <h2>Product Not Found</h2>
  );
}

const {name, img, price} = shoe;
  return (
    <div className="detail">
      <h2>{name}</h2>
      <p></p>
    <div className="Pro" >
      <img src={img} alt={name} height="500" />
      <h1>{price}</h1>
